import {VscEmptyWindow} from "react-icons/vsc"
import { usePost } from "../context/postContext"
import {Link} from "react-router-dom"
export const HomePage = () => {

  const{post} = usePost()

  if(post.length === 0) return (
    <div className="flex flex-col justify-center items-center">
      <VscEmptyWindow className="w-48 h-48 text-white"/>
      <h1 className="text-white uppercase text-2xl font-bold">
        No existen publicaciones 
      </h1>
    </div>
  )
  

  return (
    <div>
      <Link to="/new" className="bg-white rounded-sm font-bold uppercase text-black hover:bg-green-400 animate-pulse">
        Crear Publicacion
      </Link>
      
      {post.map(post=>(
        <div className="text-white font-bold text-2xl " key={post._id}>
          {post.title}
        </div>
      ))}
  
    
    </div>
  )
}
