import toast from "react-hot-toast"

const handleDelete = () =>{
    toast((t) =>(
        <div className="text-white">
            <p>Quieres eliminar?</p>
            <div>
                <button>Delete</button>
                <button className="bg-slate-400 hover:bg-slate-500 pc-3 py-2  text-white rounded-sm mx-2"  onClick={() => toast.dismiss(t.id)}>Cancelar</button>
            </div>
        </div>
    ),{
        style:{
            background: "#202020"
        }
    })
}

export const PostCard = ({post}) => {
  return (
    <div className="bg-zinc-800 text-white rounded-sm shadow-sm shadow-black hover:bg-zinc-700 hover:cursor-pointer ">
      <div className="px-4 py-7">
     <div className="flex justify-between">
     <h3>
        {post.title}
        </h3>
        <button onClick={handleDelete} className="bg-red-600 px-2 py-1 text-sm rounded-md">Delete</button>
     </div>
        <p>
        {post.description}
        </p>
       
      </div>
    </div>
  )
}
